<?phpclass OtaAccountModel extends Base_Model_Abstract{	protected $dbname = 'itourism';    protected $tblname = 'ota_account';    protected $pkKey = 'id';    protected $preCacheKey = 'cache|OtaAccountModel|';    	 public function getTable() 	 {	      return $this->tblname;	 }	 public function verify($account, $pwd) 	 {	 	$info = $this->getById($account);	 	if (!$info) Lang_Msg::error("账号不存在");	 	if ($info['pwd'] != md5($info['salt'].md5($info['salt'].$pwd))) Lang_Msg::error("密码错误");	 	return $info;	 }	     public function getUserInfoByAttributes($where = array()) {         $info = $this->get($where);         return $info;     }}